package com.eureka.provider;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.netflix.eureka.EnableEurekaClient;import org.springframework.data.jpa.repository.config.EnableJpaRepositories;/** * Created by DJ_ZJ. */@SpringBootApplication@EnableEurekaClient@EnableJpaRepositoriespublic class EurekaProviderApplication {    public static void main(String[] args){        SpringApplication.run(EurekaProviderApplication.class,args);    }}