package com.eureka.ribbon.consumer.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;/** * Created by DJ_ZJ. */@RestControllerpublic class RibbonController {    @Autowired    private RestTemplate template;    @GetMapping("/getInfo")    public Object getStudentInfo(){        /**         *   ribbon能直接通过应用名称找到应用对应的ip和端口,如果应用是通过集群搭建,         *  那么ribbon会通过轮询的方式向该应用不同的服务器上发送请求以达到负载均衡的功能         *  本次功能是向eureka-service-provider应用(服务提供者)发起查询请求         */        Object entity = template.getForObject("http://eureka-service-provider/findAll",Object.class);        return entity;    }}